CREATE TABLE Liste (idList INTEGER PRIMARY KEY, LibellePdt VARCHAR NOT NULL, CodeListe VARCHAR, FOREIGN KEY (LibellePdt) REFERENCES Produit (libellePdt));
CREATE TABLE Liste_Produit (idPdtList INTEGER PRIMARY KEY, #IdList INTEGER NOT NULL, #IdPdt INTEGER NOT NULL, #Quantite INTEGER);
CREATE TABLE Produit (idPdt INTEGER PRIMARY KEY, LibellePdt VARCHAR NOT NULL, QuantitePdt INTEGER DEFAULT 1, Predefini BOOLEAN DEFAULT 0);